import React, { useState } from 'react';
import './genre.css';

export default function GenrePredict() {
  const [audioFile, setAudioFile] = useState(null);
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setAudioFile(e.target.files[0]);
    setResult('');
  };

  const handlePredict = async () => {
    if (!audioFile) return setResult('Please upload an audio file first.');
    setLoading(true);
    setResult('');

    try {
      // ðŸ‘‡ When your Flask backend is ready, use FormData to send the file:
      // const formData = new FormData();
      // formData.append('file', audioFile);
      // const res = await fetch('http://localhost:5000/api/predict', {
      //   method: 'POST',
      //   body: formData
      // });
      // const data = await res.json();
      // setResult(`Predicted Genre: ðŸŽµ ${data.genre}`);

      // For now simulate prediction:
      await new Promise(r => setTimeout(r, 1500));
      const genres = ['Pop', 'Rock', 'Hip Hop', 'Classical', 'Jazz', 'EDM', 'Country', 'Folk'];
      const randomGenre = genres[Math.floor(Math.random() * genres.length)];
      setResult(`Predicted Genre: ðŸŽµ ${randomGenre}`);
    } catch (err) {
      setResult('Error predicting genre');
    }
    setLoading(false);
  };

  return (
    <div className="genre-container">
      <div className="genre-card">
        <h2>ðŸŽ§ Music Genre Prediction</h2>
        <p className="subtitle">Upload your audio file (WAV or MP3):</p>

        <input
          type="file"
          accept="audio/*"
          onChange={handleFileChange}
        />

        <button onClick={handlePredict} disabled={loading}>
          {loading ? 'Predicting...' : 'Predict Genre'}
        </button>

        {result && <div className="result-box">{result}</div>}
      </div>
    </div>
  );
}
