import React, { useState } from 'react';
import '.genre.css';

export default function GenrePredict() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!input.trim()) return setResult('Please enter a song name or YouTube link');
    setLoading(true);
    setResult('');

    try {
      // ðŸ‘‡ Later, replace this with your Flask model API endpoint
      // For now weâ€™ll simulate a prediction
      await new Promise(r => setTimeout(r, 1500));
      const genres = ['Pop', 'Rock', 'Hip Hop', 'Classical', 'Jazz', 'EDM', 'Country', 'Folk'];
      const randomGenre = genres[Math.floor(Math.random() * genres.length)];
      setResult(`Predicted Genre: ðŸŽµ ${randomGenre}`);
    } catch (err) {
      setResult('Error predicting genre');
    }
    setLoading(false);
  };

  return (
    <div className="genre-container">
      <div className="genre-card">
        <h2>ðŸŽ§ Music Genre Prediction</h2>
        <p className="subtitle">Enter a song name or YouTube link below:</p>

        <input
          type="text"
          placeholder="e.g., Shape of You or https://youtube.com/..."
          value={input}
          onChange={e => setInput(e.target.value)}
        />

        <button onClick={handlePredict} disabled={loading}>
          {loading ? 'Predicting...' : 'Predict Genre'}
        </button>

        {result && <div className="result-box">{result}</div>}
      </div>
    </div>
  );
}
