import { useState } from 'react';
import './genre.css';

export default function GenrePredict() {
  const [audioFile, setAudioFile] = useState(null);
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setAudioFile(e.target.files[0]);
    setResult('');
  };

  const handlePredict = async () => {
    if (!audioFile) return setResult('Please upload an audio file first.');
    setLoading(true);
    setResult('');

    try {
      // Create FormData and append file
      const formData = new FormData();
      formData.append('file', audioFile); // âœ… matches Flask backend

      // Send POST request to Flask backend
      const res = await fetch('http://127.0.0.1:5001/predict', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) throw new Error('Server error');

      const data = await res.json();
      setResult(`Predicted Genre: ðŸŽµ ${data.genre}`);
    } catch (err) {
      console.error(err);
      setResult('Error predicting genre. Please try again.');
    }

    setLoading(false);
  };

  return (
    <div className="genre-container">
      <div className="genre-card">
        <h2>ðŸŽ§ Music Genre Prediction</h2>
        <p className="subtitle">Upload your audio file (WAV or MP3):</p>

        <input
          type="file"
          accept="audio/*"
          onChange={handleFileChange}
        />

        <button onClick={handlePredict} disabled={loading}>
          {loading ? 'Predicting...' : 'Predict Genre'}
        </button>

        {result && <div className="result-box">{result}</div>}
      </div>
    </div>
  );
}
